import React from "react";
import ReactDOM from "react-dom";

import "./styles.css";
import SideBar from "./sidebar";
import Gallery from "./gallery.js"

function App() {
  function onMenuOpen(){
    window.onMenuOpened();
  }

  return (
    <div id="outer-container">
      <SideBar pageWrapId={"page-wrap"}/>
      <div id="page-wrap">
        <Gallery/>
      </div>
    </div>
  );
}
