import React from "react";
import QuickLinks from "./contact.js";
import Section from "./section.js";
import Tool from "./tools.js";

//burger menu
//push, pushRotate, elastic
import { push as Menu } from "react-burger-menu";

//material
import Grid from "@mui/material/Grid";
import Stack from "@mui/material/Stack";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";

//window calculation
import useWindowDimensions from "./menuWindow.js";
import { useSafeAreaInsets } from "react-native-safe-area-context";
import measurementToGoldenRatio from "./golden.js";

//icon
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowAltCircleRight } from "@fortawesome/free-solid-svg-icons";
import {} from "@fortawesome/free-regular-svg-icons";
import {} from "@fortawesome/fontawesome-svg-core";

const sxSpacing = {mt:"4px", mr:"24px"};

const mobile = <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
            <Box sx={sxSpacing}>
            <Tool tool="dart" />
            </Box>
        <Tool tool="flutter" />
        <Tool tool="swift" />
            </Box>;

        const backend = <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
        <Tool tool="firebase" />
        <Tool tool="aws" />
        </Box>;

const web = <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
            <Tool tool="react" />
            <Tool tool="node" />
            <Tool tool="rails" />
            <Tool tool="dotNet" />
            <Tool tool="php" />
            <Tool tool="js" />
            <Tool tool="html" />
            <Tool tool="css" />
            </Box>;

const game = <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
            <Tool tool="unity" />
            <Tool tool="cSharp" />
            </Box>;

            const ide = <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
            <Tool tool="vsCode" />
            <Tool tool="visualStudios" />
            </Box>;

const versionControl =  <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
            <Tool tool="github" />
        <Tool tool="bitbucket" />
        </Box>;

const builder = <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
        <Tool tool="webflow" />
        <Tool tool="wordpress" />
        <Tool tool="shopify" />
        <Tool tool="wix" />
        </Box>;

const graphic = <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
          <Tool tool="photoshop" />
          <Tool tool="premiere" />
          </Box>;

          const other = <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
            <Tool tool="java" />
            <Tool tool="python" />
            <Tool tool="cPlusPlus" />
            <Tool tool="c" />
            </Box>;

//widget
export default function SideBar(props) {
  //everything slide related below
  const screenSize = useWindowDimensions();
  const screenWidth = screenSize.width;
  const screenHeight = screenSize.height;

  //remove insets
  const insets = useSafeAreaInsets();
  const actualWidth = screenWidth - insets.left - insets.right;
  const actualHeight = screenHeight - insets.top - insets.bottom;

  //calculate header height
  var headerHeight = measurementToGoldenRatio({ value: actualHeight }).small;

  //ideally we want to be visually pleasing
  var largestMenuWidth = measurementToGoldenRatio({ value: actualWidth }).small;

  const smallestDesiredMenuWidth = 260;
